/*! HTML5 Boilerplate v8.0.0 | MIT License | https://html5boilerplate.com/ */

/* main.css 2.1.0 | MIT License | https://github.com/h5bp/main.css#readme */
/*
 * What follows is the result of much research on cross-browser styling.
 * Credit left inline and big thanks to Nicolas Gallagher, Jonathan Neal,
 * Kroc Camen, and the H5BP dev community and team.
 */

/* ==========================================================================
   Base styles: opinionated defaults
   ========================================================================== */

html {
  color: #222;
  font-size: 1em;
  line-height: 1.4;
}

/*
 * Remove text-shadow in selection highlight:
 * https://twitter.com/miketaylr/status/12228805301
 *
 * Vendor-prefixed and regular ::selection selectors cannot be combined:
 * https://stackoverflow.com/a/16982510/7133471
 *
 * Customize the background color to match your design.
 */

::-moz-selection {
  background: #b3d4fc;
  text-shadow: none;
}

::selection {
  background: #b3d4fc;
  text-shadow: none;
}

/*
 * A better looking default horizontal rule
 */

hr {
  display: block;
  height: 1px;
  border: 0;
  border-top: 1px solid #ccc;
  margin: 1em 0;
  padding: 0;
}

/*
 * Remove the gap between audio, canvas, iframes,
 * images, videos and the bottom of their containers:
 * https://github.com/h5bp/html5-boilerplate/issues/440
 */

audio,
canvas,
iframe,
img,
svg,
video {
  vertical-align: middle;
}

/*
 * Remove default fieldset styles.
 */

fieldset {
  border: 0;
  margin: 0;
  padding: 0;
}

/*
 * Allow only vertical resizing of textareas.
 */

textarea {
  resize: vertical;
}

/*
 * Others
 */
ul {
  list-style-type: none;
  padding-inline: 12px;
}

a:-webkit-any-link {
  color: inherit;
  text-decoration: none;
  cursor: pointer;
}

/* ==========================================================================
   Author's custom styles
   ========================================================================== */

	/*-------------------------------------------------------*/
		/* Base styles	 */
	/*-------------------------------------------------------*/
	html {
		overflow-y:scroll;
	}

	body {
		background-image:url(../images/bg.png);
		background-color: #999;
		margin: 0px;
		padding: 0px;
		font-family:'Open Sans', Arial;
		font-size: 12px;
		-webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
	}

	/* Font face */
	@font-face {
		font-family: 'icomoon';
		src: url('../fonts/icomoon/icomoon.eot');
		src: url('../fonts/icomoon/icomoon.eot?#iefix') format('embedded-opentype'),
					url('../fonts/icomoon/icomoon.svg#icomoon') format('svg'),
					url('../fonts/icomoon/icomoon.woff') format('woff'),
					url('../fonts/icomoon/icomoon.ttf') format('truetype');
	}

	/*-------------------------------------------------------*/
		/* Wrap */
	/*-------------------------------------------------------*/
	#container {
		margin:0 auto;
		width:860px;
		margin-bottom: 20px;
		margin-top: 30px;
	}
	#content {
		background-color:#FFF;
		-webkit-box-shadow: 0 1px 6px rgba(0, 0, 0, 0.27), 0 0 40px rgba(0, 0, 0, 0.06) inset;
		-moz-box-shadow: 0 1px 6px rgba(0, 0, 0, 0.27), 0 0 40px rgba(0, 0, 0, 0.06) inset;
		box-shadow: 0 1px 6px rgba(0, 0, 0, 0.27), 0 0 40px rgba(0, 0, 0, 0.06) inset;
	}
	/*-------------------------------------------------------*/
		/* Generic Classes */
	/*-------------------------------------------------------*/
	.hidden {
		display: none;
	}

	/* Clearfix */

	.clear{
	  clear:both;
	  display:block;
	  height:0;
	  overflow:
	  hidden;
	  visibility:hidden;
	  width:0
	}
	.clearfix:after{
	  clear:both;
	  content:' ';
	  display:block;
	  font-size:0;
	  height:0;
	  line-height:0;
	  visibility:hidden;
	  width:0
	}
	* html .clearfix,:first-child+html .clearfix{
	  zoom:1
	}
	.extra-text {
		font-size:20px;
		padding:24px 0;
    border-top: 1px solid #ededed;
    border-bottom: 1px solid #ededed;
		color: #999;
		margin-bottom:20px;
		font-weight: 300;
	}
    .transition {
    	-webkit-transition:all .5s ease;
	     -moz-transition:all .5s ease;
	      -ms-transition:all .5s ease;
	       -o-transition:all .5s ease;
	          transition:all .5s ease;
    }
    .word-wrap {
		word-wrap: break-word;
		-ms-word-break: break-all;
		     word-break: break-all;
		-webkit-hyphens: auto;
		   -moz-hyphens: auto;
		        hyphens: auto;
    }
	/*-------------------------------------------------------*/
		/* Typography */
	/*-------------------------------------------------------*/
  h1 {
		font-size: 37px;
		font-weight: 900;
	  margin-block: 0;
    margin-inline: 0;
	}
	h2 {
		font-size: 30px;
		font-weight: 900;
	  margin-block: 0;
    margin-inline: 0;
	}
	h3 {
		font-size: 20px;
		font-weight: 600;
	  margin-block: 0;
    margin-inline: 0;
	}
	h4 {
		font-size: 16px;
		font-weight: 400;
	  margin-block: 0;
    margin-inline: 0;
	}
	h5 {
		font-size: 14px;
		font-weight: 500;
	  margin-block: 0;
    margin-inline: 0;
